import './PopupMenu.css';
import React, { useEffect, useState } from 'react';
import { MenuContext, PathName } from '../../contexts/CurrentUserContext';
import { NavLink, useNavigate } from 'react-router-dom';


function PopupMenu({setPopupMenuOpen, activeLink, setActiveLink}) {

  const menu = React.useContext(MenuContext);
  const pathName = React.useContext(PathName);



  // const handleLinkClick = (index) => {
  //   setActiveLink(index);
  //   // Сохраняем значение в localStorage при каждом клике на ссылку
  //   localStorage.setItem('activeLink', index);
  //   setPopupMenuOpen(false);
  // };

  // console.log(activeLink)

  return (
    <div className={`popup ${menu ? 'popup_opened' : ''}`}>
      <div className='conteiner__menu'>
        <ul className='navigation__links'>
          <NavLink to='/' className={`navigation__link effect ${pathName === 0 ? 'decoration' : ''}`} onClick={() => handleLinkClick(0)}>Главная</NavLink>
          <NavLink to='/movies' className={`navigation__link effect ${activeLink === 1 ? 'decoration' : ''}`} onClick={() => handleLinkClick(1)}>Фильмы</NavLink>
          <NavLink to='/saved-movies' className={`navigation__link effect ${activeLink === 2 ? 'decoration' : ''}`} onClick={() => handleLinkClick(2)}>Сохранённые фильмы</NavLink>
        </ul>
        <div className='conteiner__account'>
          <NavLink to='/profile' className={`navigation-button__account effect ${activeLink === 3 ? 'navigation_active' : ''}`} onClick={() => handleLinkClick(3)}>Аккаунт</NavLink>
        </div>
      </div>
    </div>
  )
}

export default PopupMenu;
