import './Profile.css';
import React from 'react';
import { useNavigate, } from 'react-router-dom';
import { AppContext } from '../../contexts/CurrentUserContext';
import profileForm from './profileForm/profileForm';
import ButtonEditAndExit from './ButtonEditAndExit/ButtonEditAndExit';

function Profile({ setLoggedIn }) {

  const currentUser = React.useContext(AppContext);
  const [name, setName] = React.useState('');
  const [email, setEmail] = React.useState('');
  const history = useNavigate();
  const [submit, setSubmit] = React.useState(false)

  React.useEffect(() => {
    if (currentUser) {
      setName(currentUser.name);
      setEmail(currentUser.email);
    }
  }, [currentUser]);


  function handleNameChange(e) {
    setName(e.target.value);
  }

  function handleEmailChange(e) {
    setEmail(e.target.value);
  }

  function signOut() {
    setLoggedIn(false);
    history('/', { replace: true })
  }

  function editProfile() {
    setSubmit(true)
  }

  function formExit() {
    setSubmit(false)
  }

  return (
    <main className='profile'>
      {submit
        ?
        <>
          <h1 className='profile__title'>Привет, {currentUser.name} !</h1>
          <conteiner className='profile__conteiner-info' >
            <div className='profile__user'>
              <h2 className='profile__user-info'>Имя</h2>
              <p required className='profile__current-user'>{currentUser.name}</p>
            </div>
            <div className='profile__user'>
              <h2 className='profile__user-info'>E-mail</h2>
              <p required className='profile__current-user'>{currentUser.email}</p>
            </div>
          </conteiner>
          <button className='profile__edit  effect profile__edit_error' type="button" onClick={editProfile}>Редактировать</button>
          <div className='profile__logout  effect' onClick={signOut}>Выйти из аккаунта</div>
        </>
        :
        <profileForm name/>
      }
    </main>
  )
}

export default Profile;
