import './FormProfile.css';
import React from 'react';

function FormProfile(props) {

  const [message, setMessage] = React.useState('');
  const [nameError, setNameError] = React.useState('');
  const [emailError, setEmailError] = React.useState('');
  const [passwordError, setPasswordError] = React.useState('');
  const [formNotValid, setformNotValid] = React.useState(true);

  React.useEffect(() => {
    if (message) {
      setTimeout(() => {
        setMessage('');
      }, 4500);
    }
  }, [message])

  return (
    <>
      <form className='profile__form' >
        <div className='profile__field'>
          <label className='profile__info'>Имя</label>
          <input required
          className='profile__input'
          type="text"
          value={props.name}
          onChange={props.handleNameChange}
          name="name"
          minLength="2"
          maxLength="30" />
        </div>
        <div className='profile__field'>
          <label className='profile__info'>E-mail</label>
          <input required
          className='profile__input'
          type="text"
          value={props.email}
          onChange={props.handleEmailChange}
          name="email"
          pattern="[a-z0-9]+@[a-z]+\.[a-z]{2,}" />
          <p className='form__input_error'>{emailError}</p>
        </div>
        <conteiner className='button-submit__conteiner'>
          <p className='button-submit__error'>{props.emailError}</p>
          <button className='button-submit effect' type='submit'>Сохранить</button>
          <p className='form__input_error'>{emailError}</p>
        </conteiner>
      </form>
      <div className='form__exit effect' onClick={props.formExit}>Отменить редактирование профиля</div>
    </>
  )
}

export default FormProfile;
