import './Profile.css';
import React from 'react';
import { AppContext } from '../../contexts/CurrentUserContext';

function Profile() {

  const currentUser = React.useContext(AppContext);

  return (
    <main className='profile'>
      <h1 className='profile__title'>Привет, {currentUser.name} !</h1>
      <form className='profile__form' onSubmit={handleSubmit}>
        <div className='profile__field'>
          <label className='profile__info'>Имя</label>
          <input required
            value={name}
            type="text"
            minLength="2"
            maxLength="30"
            placeholder='Имя'
            onChange={handleName}
            className='profile__input'
          ></input>
        </div>
        <p className='register__error'>{nameError}</p>
        <div className='profile__field'>
          <label className='profile__info'>E-mail</label>
          <input required
            value={email}
            type="email"
            placeholder='E-mail'
            pattern="[a-z0-9]+@[a-z]+\.[a-z]{2,}"
            onChange={handleEmail}
            className='profile__input'
          />
        </div>
        <p className='register__error'>{emailError}</p>
        <button className='profile__edit  effect profile__edit_error'type="submit">Редактировать</button>
      </form>
      <div className='profile__logout  effect'>Выйти из аккаунта</div>
    </main>
  )
}

export default Profile;
