import './SearchForm.css';
import React from "react";
import MoviesButton from '../../../images/find-3.svg';

function SearchForm(props) {
  const movieRef = React.useRef();

  function handleSubmit(e) {
    e.preventDefault();

    const movie = movieRef.current.value;

    props.onGetMovie({
      movie: movie
    });

  }

  return (
    <form className="movies-form" onSubmit={handleSubmit}>
      <input className="movies-form__input" ref={movieRef} type='text' placeholder='Фильм' />
      <button className="movies-form__button" onC>
        <img src={MoviesButton} alt="Кнопка поиска" />
      </button>
    </form>
  )
}

export default SearchForm;
