import './Navigation.css';
import React from 'react';
import { MenuContext } from '../../../contexts/CurrentUserContext';

function Navigation(props) {

  const menu = React.useContext(MenuContext);

  const body = document.querySelector('body')

  const hideOverflow = () => {
    body.style={"overflow": hidden}
  }

  return (
    <>
      <div className="navigation">
        <div className='navigation__movies'>
          <div className='conteiner__movies'>
            <button className='navigation__movies-button navigation__movies-button_search effect' type="button">Фильмы</button>
            <button className='navigation__movies-button navigation__movies-button_saved effect' type="button">Сохранённые фильмы</button>
          </div>
        </div>
        <button className='navigation-button__account effect' type="button">Аккаунт</button>
      </div>
      <div className={`navigation__movies-burger ${menu ? 'navigation__movies-burger_position active' : ''}`} onClick={props.toggleBurger}>
        <span></span>
      </div>
    </>
  )
}

export default Navigation;
