import './Navigation.css';
import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { MenuContext } from '../../../contexts/CurrentUserContext';

function Navigation(props) {

  const menu = React.useContext(MenuContext);


  return (
    <>
      <div className="navigation">
        <div className='navigation__movies'>
          <div className='conteiner__movies'>
            <NavLink to='/movies' className='navigation__movies-button navigation__movies-button_search effect' type="button">Фильмы</NavLink>
            <NavLink ro className='navigation__movies-button navigation__movies-button_saved effect' type="button">Сохранённые фильмы</NavLink>
          </div>
        </div>
        <NavLink className='navigation-button__account effect' type="button">Аккаунт</NavLink>
      </div>
      <div className={`navigation__movies-burger ${menu ? 'navigation__movies-burger_position navigation__movies-burger_position_active' : ''}`} onClick={props.toggleBurger} >
        <span></span>
      </div>
    </>
  )
}

export default Navigation;
