import React from 'react';
import './App.css';
import { Routes, Route, useNavigate, Navigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { AppContext, MenuContext, LoggedInContext, PathName } from '../../contexts/CurrentUserContext';
import Header from '../Header/Header';
import Main from '../Main/Main';
import Footer from '../Footer/Footer';
import PopupMenu from '../PopupMenu/PopupMenu';
import Movies from '../Movies/Movies';
import ProtectedRoute from '../ProtectedRoute';
import PageNotFound from '../PageNotFound/PageNotFound';
import Profile from '../Profile/Profile';

function App() {
  const body = document.body;

  const [isPopupMenuOpen, setPopupMenuOpen] = React.useState(false);


  const [currentUser, setCurrentUser] = useState({});
  const [loggedIn, setLoggedIn] = useState(true);
  const [currentPath, setCurrentPath] = useState('');


  const toggleBurger = () => {
    setPopupMenuOpen(!isPopupMenuOpen);
    if (isPopupMenuOpen) {
      body.classList.remove('overflow');
    } else {
      body.classList.add('overflow');
    }
  }

  const getInitialActiveLink = () => {
    const currentPath = window.location.pathname;
    // Определить, какой пункт активен в зависимости от текущего пути
    if (currentPath === '/movies') {
      return 1;
    } else if (currentPath === '/saved-movies') {
      return 2;
    } else if (currentPath === '/profile') {
      return 3;
    } else {
      return 0;
    }
  };

  const [activeLink, setActiveLink] = useState(getInitialActiveLink);


  return (
    <>
      <MenuContext.Provider value={isPopupMenuOpen}>
        <AppContext.Provider value={currentUser}>
          <LoggedInContext.Provider value={loggedIn}>
            <div className='page'>
              <Routes>
                <Route path='/' element={
                  <>
                    <Header toggleBurger={toggleBurger} setActiveLink={setActiveLink} />
                    <Main />
                    <Footer />
                  </>
                } />
                <Route path='/movies' element={
                  <ProtectedRoute>
                    <Header toggleBurger={toggleBurger} setActiveLink={setActiveLink} />
                    <Movies />
                    <Footer />
                  </ProtectedRoute>
                } />
                <Route path='/saved-movies' element={
                  <ProtectedRoute>
                    <Header toggleBurger={toggleBurger} setActiveLink={setActiveLink} />
                    <Movies />
                    <Footer />
                  </ProtectedRoute>
                } />
                <Route path='/profile' element={
                  <ProtectedRoute>
                    <Header toggleBurger={toggleBurger} setActiveLink={setActiveLink} />
                    <Profile />
                  </ProtectedRoute>
                } />
                <Route path='*' element={
                  <PageNotFound />
                } />
              </Routes>
              <PopupMenu setPopupMenuOpen={setPopupMenuOpen} activeLink={activeLink} setActiveLink={setActiveLink} />
            </div>
          </LoggedInContext.Provider>
        </AppContext.Provider>
      </MenuContext.Provider>
    </>
  )
}

export default App;
