import './PopupMenu.css';
import React, { useEffect, useState } from 'react';
import { MenuContext } from '../../contexts/CurrentUserContext';
import { NavLink, useNavigate } from 'react-router-dom';


function PopupMenu({setPopupMenuOpen, activeLink, setActiveLink}) {

  const menu = React.useContext(MenuContext);



  const handleLinkClick = (index) => {
    setActiveLink(index);
    // Сохраняем значение в localStorage при каждом клике на ссылку
    localStorage.setItem('activeLink', index);
    props.setPopupMenuOpen(false);
  };

  return (
    <div className={`popup ${menu ? 'popup_opened' : ''}`}>
      <div className='conteiner__menu'>
        <ul className='navigation__links'>
          <NavLink to='/' className={`navigation__link ${props.activeLink === 0 ? 'decoration' : ''} effect`} onClick={() => handleLinkClick(0)}>Главная</NavLink>
          <NavLink to='/movies' className={`navigation__link ${props.activeLink === 1 ? 'decoration' : ''} effect`} onClick={() => handleLinkClick(1)}>Фильмы</NavLink>
          <NavLink to='/saved-movies' className={`navigation__link ${props.activeLink === 2 ? 'decoration' : ''} effect`} onClick={() => handleLinkClick(2)}>Сохранённые фильмы</NavLink>
        </ul>
        <div className='conteiner__account'>
          <button className='navigation__account effect'>Аккаунт</button>
        </div>
      </div>
    </div>
  )
}

export default PopupMenu;
