import React from 'react';
import './App.css';
import { Routes, Route, useNavigate, Navigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { AppContext, MenuContext, LoggedInContext } from '../../contexts/CurrentUserContext'
import Header from '../Header/Header';
import Main from '../Main/Main';
import Footer from '../Footer/Footer';
import PopupMenu from '../PopupMenu/PopupMenu'
import Movies from '../Movies/Movies'

function App() {
  const [isPopupMenuOpen, setPopupMenuOpen] = React.useState(false);


  const [currentUser, setCurrentUser] = useState({});
  const [loggedIn, setLoggedIn] = useState(true);

  const toggleBurger = () => {
    setPopupMenuOpen(!isPopupMenuOpen);
  }

  const getInitialActiveLink = () => {
    const currentPath = window.location.pathname;
    // Определите, какой пункт активен в зависимости от текущего пути
    if (currentPath === '/movies') {
      return 1;
    } else if (currentPath === '/saved-movies') {
      return 2;
    } else {
      return 0;
    }
  };

  const [activeLink, setActiveLink] = useState(getInitialActiveLink);


  return (
    <>
      <MenuContext.Provider value={isPopupMenuOpen}>
        <AppContext.Provider value={currentUser}>
          <LoggedInContext.Provider value={loggedIn}>
            <div className='page'>
              <Routes>
                <Route path='/' element={
                  <>
                    <Header toggleBurger={toggleBurger} />
                    <Main />
                    <Footer />
                  </>
                } />
                <Route path='/movies' element={
                  <>
                    <Header toggleBurger={toggleBurger} />
                    <Movies />
                    <Footer />
                  </>
                } />
                <Route path='/saved-movies' element={
                  <>
                    <Header toggleBurger={toggleBurger} />
                    <Movies />
                    <Footer />
                  </>
                } />
              </Routes>
              <PopupMenu setPopupMenuOpen={setPopupMenuOpen} activeLink={activeLink}/>
            </div>
          </LoggedInContext.Provider>
        </AppContext.Provider>
      </MenuContext.Provider>
    </>
  )
}

export default App;
