import './Profile.css';
import React from 'react';
import { useNavigate, } from 'react-router-dom';
import { AppContext } from '../../contexts/CurrentUserContext';
import FormProfile from './FormProfile/FormProfile';
import InfoProfile from './InfoProfile/InfoProfile'

function Profile({ setLoggedIn }) {

  const currentUser = React.useContext(AppContext);
  const [name, setName] = React.useState('');
  const [email, setEmail] = React.useState('');
  const history = useNavigate();
  const [submit, setSubmit] = React.useState(false)

  React.useEffect(() => {
    if (currentUser) {
      setName(currentUser.name);
      setEmail(currentUser.email);
    }
  }, [currentUser]);


  function handleNameChange(e) {
    setName(e.target.value);
  }

  function handleEmailChange(e) {
    setEmail(e.target.value);
  }

  function signOut() {
    setLoggedIn(false);
    history('/', { replace: true })
  }

  function editProfile() {
    setSubmit(true)
  }

  function formExit() {
    setSubmit(false)
  }

  return (
    <main className='profile'>
      <h1 className='profile__title'>Привет, {currentUser.name} !</h1>
      {submit
        ?
        <FormProfile name={name} email={email} handleNameChange={handleNameChange} handleEmailChange={handleEmailChange} formExit={formExit} />
        :
        <InfoProfile signOut={signOut} />
      }
    </main>
  )
}

export default Profile;
