import React, { useState, useEffect } from "react";
import './Movies.css';
import SearchForm from './SearchForm/SearchForm';
import FilterCheckbox from './FilterCheckbox/FilterCheckbox';
import Preloader from './Preloader/Preloader';
import MoviesCardList from './MoviesCardList/MoviesCardList';
import getBeatFilmMovies from '../../utils/getBeatFilmMovies';

function Movies() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [renderedMovies, setRenderedMovies] = useState([]);
  const [visibleMovies, setVisibleMovies] = useState(0);
  const [error, setError] = useState(null);

  async function onGetMovie() {
    setLoading(true);
    setError(null);

    try {
      const fetchedData = await getBeatFilmMovies();
      setData(fetchedData);
      setVisibleMovies(8); // Начальное количество видимых карточек
    } catch (error) {
      setError("Во время запроса произошла ошибка. Возможно, проблема с соединением или сервер недоступен. Подождите немного и попробуйте ещё раз");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    if (data) {
      // Устанавливаем начальные карточки для отображения
      setRenderedMovies(data.slice(0, visibleMovies));
    }
  }, [data, visibleMovies]);

  const handleShowMore = () => {
    // При нажатии на кнопку "Ещё" отобразить дополнительные карточки
    setVisibleMovies(prev => prev + 2);
  };

  return (
    <div className='movies'>
      <div className='movies-conteiner'>
        <SearchForm onGetMovie={onGetMovie} />
        <FilterCheckbox />
        <div className="line"></div>
      </div>
      <div className="movies-card__list">
        {loading && <Preloader />}
        {error && <p>{error}</p>}
        {renderedMovies.length > 0 &&
          <MoviesCardList data={renderedMovies} />
        }
        {data && visibleMovies < data.length &&
          <button className="show-more-button" onClick={handleShowMore}>Ещё</button>
        }
      </div>
    </div>
  );
}

export default Movies;
