import React from "react";
import './Movies.css';
import SearchForm from './SearchForm/SearchForm';
import FilterCheckbox from './FilterCheckbox/FilterCheckbox';
import Preloader from './Preloader/Preloader';
import MoviesCardList from './MoviesCardList/MoviesCardList';
import getBeatFilmMovies from '../../utils/getBeatFilmMovies';

function Movies(props) {

  const [data, setData] = React.useState(null);
  const [loading, setLoading] = React.useState(null);



  // function onGetMovie() {
  //   getBeatFilmMovies()
  //     .then((data) => {
  //       setLoading(true)
  //       // тут мне нужно, чтобы Preloader работал 3 секунды, пока делается запрос на сервер, а потом уже абрать его!
  //       setData(data);
  //       setLoading(false);
  //     })
  //     .catch(console.error);
  // }

  async function onGetMovie() {
    setLoading(true);

    try {
      getBeatFilmMovies()
      .then((data) => {
        setLoading(true)
        // тут мне нужно, чтобы Preloader работал 3 секунды, пока делается запрос на сервер, а потом уже абрать его!
        setData(data);
      })
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  }

  console.log(data);

  return (
    <div className='movies'>
      <div className='movies-conteiner'>
        <SearchForm  onGetMovie={onGetMovie} />
        <FilterCheckbox />
        <div className="line"></div>
      </div>
      <div className="movies-card__list">
      {loading ? <Preloader /> : <MoviesCardList data={data} />}
      </div>
    </div>
  )
}

export default Movies;
