import React from 'react';
import './Header.css';
import HeaderLog from '../../images/logo.svg';
import Navigation from './Navigation/Navigation';
import ButtonAutchLogg from './ButtonAutchLogg/ButtonAutchLogg';
import { NavLink } from 'react-router-dom';
import {LoggedInContext} from '../../contexts/CurrentUserContext'

function Header(props) {

  const loggedIn = React.useContext(LoggedInContext);

  const handleLinkClick = (index) => {
    setActiveLink(index);
    // Сохраняем значение в localStorage при каждом клике на ссылку
    localStorage.setItem('activeLink', index);
    setPopupMenuOpen(false);
  };

  return (
    <div className="header">
      <section className='header__user'>
        <NavLink to='/'>
          <img className="logo logo_place_header" src={HeaderLog} alt="Логотип" onClick={()=> handleLinkClick(0)} />
        </NavLink>
        <div className='header__user-movies'>
          {loggedIn
            ? <Navigation toggleBurger={props.toggleBurger} handleLinkClick={handleLinkClick}/>
            : <ButtonAutchLogg />
          }
        </div>
      </section>
    </div>
  )
}

export default Header;
