import './Register.css';
import HeaderLog from '../../images/logo.svg';
import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import Form from '../Form/Form';

function Register(props) {

  const history = useNavigate();
  const [name, setName] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');
  const [message, setMessage] = React.useState("");
  const [nameError, setNameError] = React.useState('');
  const [emailError, setEmailError] = React.useState("");
  const [passwordError, setPasswordError] = React.useState("");
  const [formNotValid, setformNotValid] = React.useState(true);


  function handleEmail(e) {
    setEmail(e.target.value);
    if (e.target.validationMessage === "Введите данные в указанном формате.") {
      setEmailError(`${e.target.validationMessage} Например: user@mail.ru`);
    } else {
      setEmailError(e.target.validationMessage);
    }
  }

  function handlePassword(e) {
    setPassword(e.target.value);
    setPasswordError(e.target.validationMessage);
  }


  return (
    <>
      <header className='header__register'>
        <NavLink to='/'>
          <img className="logo logo_place_header" src={HeaderLog} alt="Логотип" onClick={() => props.handleLinkClick(0)} />
        </NavLink>
        <h1 className='register__title'>Добро пожаловать!</h1>
      </header>
      <Form nameFor='register__form' nameId="registerForm" formId="registerForm" submitButtonLabel="Зарегистрироваться" >
        <label className="form__wrapper">
          <input required
          className="form__input form__input_text_name"
          onChange={handleChange}
          type="text"
          name="name"
          minLength="2"
          maxLength="30"  />
          <div className={`input__label ${name === '' ? '' : 'position'}`}>Имя</div>
          <p className='form__input_error'>{errors.name}</p>
        </label>
        <label className="form__wrapper">
          <input required
          className="form__input form__input_text_email"
          onChange={handleEmail}
          type="text"
          name="email"
          pattern="[a-z0-9]+@[a-z]+\.[a-z]{2,}" />
          <div className={`input__label ${email === '' ? '' : 'position'}`}>E-mail</div>
          <p className='form__input_error'>{emailError}</p>
        </label>
        <label className="form__wrapper">
          <input required
          className="form__input form__input_text_password"
          onChange={handlePassword}
          type="password"
          name="password"
          minLength="5"
          maxLength="12" />
          <div className={`input__label ${password === '' ? '' : 'position'}`}>Password</div>
          <p className='form__input_error'>{passwordError}</p>
        </label>
      </Form>
      <div className="informationSupport">
        <p className="informationSupport__text informationSupport__text-question">Уже зарегистрированы?</p>
        <NavLink to='/signin' className="informationSupport__text informationSupport__text-login effect">Войти</NavLink>
      </div>
    </>
  )

}

export default Register;
